create table applicant (id number(19,0) not null, current_state varchar2(255 char), email varchar2(255 char), name varchar2(255 char), phone_number varchar2(255 char), primary key (id));
create table appointment (id number(19,0) not null, appointment_date timestamp, confirmed number(1,0) not null, description varchar2(255 char), location varchar2(255 char), user_id number(19,0), primary key (id));
create table partitionjobtable (id number(10,0) not null, age number(10,0), password varchar2(255 char), username varchar2(255 char), primary key (id));
create table person (id number(19,0) not null, first_name varchar2(255 char), last_name varchar2(255 char), primary key (id));
create table primary_account (id number(19,0) not null, account_balance number(19,2), account_number number(10,0) not null, primary key (id));
create table primary_transaction (id number(19,0) not null, amount double precision not null, available_balance number(19,2), description varchar2(255 char), primary_transaction_date timestamp, status varchar2(255 char), type varchar2(255 char), primary_account_id number(19,0), primary key (id));
create table recipient (id number(19,0) not null, account_number varchar2(255 char), description varchar2(255 char), email varchar2(255 char), name varchar2(255 char), phone varchar2(255 char), user_id number(19,0), primary key (id));
create table role (role_id number(10,0) not null, name varchar2(255 char), primary key (role_id));
create table savings_account (id number(19,0) not null, account_balance number(19,2), account_number number(10,0) not null, primary key (id));
create table savings_transaction (id number(19,0) not null, amount double precision not null, available_balance number(19,2), description varchar2(255 char), saving_transaction_dates timestamp, status varchar2(255 char), type varchar2(255 char), saving_account_id number(19,0), primary key (id));
